</code></pre> <pre><code>pair&lt;int, int&gt; twopt(const vector&lt;int&gt;&amp; nums, int target) {
    int l = 0, r = nums.size() - 1;
    while (l &lt; r) {
        int sum = nums[l] + nums[r];
        if (sum == target) {
            return {l, r};
        } else if (sum &lt; target) {
            l++;
        } else {
            r--;
        }
    }
    return {-1, -1};
}
